(window.webpackJsonp=window.webpackJsonp||[]).push([[12],{183:function(e,n,i){"use strict";i.r(n);var s=i(2),t=Object(s.a)({},function(){var e=this,n=e.$createElement,i=e._self._c||n;return i("div",{staticClass:"content"},[e._m(0),i("h3",{attrs:{id:"wie-war-das-eigentlich-mit-diesem-ikarus"}},[e._v("Wie war das eigentlich mit diesem Ikarus?")]),i("p",[e._v("Die groben Züge von Ovids Metamorphosis kennt wahrscheinlich jeder: Dädalus und Ikarus wurden in Minos Labyrinth verbannt – Dädalus bastelte beiden Flügel – Ikarus  flog zu nah an die Sonne, stürzte ab und ertrank.")]),i("p",[e._v("Aber ich möchte es ein Quäntchen genauer aufdröseln. Dazu habe ich mal in Ovids Metamorphosen nachgelesen. Man sollte vorher etwas über Minos wissen, bevor man sich mit Dädalus und seinem Sohn Ikarus beschäftigt. Minos wollte König von Kreta werden, sein Bruder allerdings auch. Um diesen Wettstreit für sich zu entscheiden bat er Poseidon um Hilfe. So begab es sich, dass er als Opfertier einen stattlichen weißen Stier bekam, den es Poseidon zu Opfern galt, um seine Schuld zu begleichen. Der Stier gefiel Minos jedoch so sehr, dass er ihn nicht töten wollte. Erzürnt über diesen Frevel verleitete Poseidon Minos´ Frau Pasiphae dazu, eine Liebschaft mit diesem Stier einzugehen. Da Stiere und Menschen jedoch wenig kompatibel sind, brauchte Pasiphae eine List. Sie bat Dädalus um Hilfe und er baute ihr ein weibliches hölzernes Gegenstück zu dem weißen Stier. In diesem versteckte sie sich und ging so eine Verbindung mit dem Tier ein. Aus diesem geteilten Lager entsprang ein Kind, der Minotaurus. Erzürnt über sein Weib und die Frucht ihres Schoßes ließ er seinerseits ein Labyrinth von Dädalus erbauen, in dem er die Missgestalt verstecken konnte.")]),i("p",[e._v("An dieser Stelle wird’s ein bisschen tricky, weil ich nicht genau weiß weshalb Minos Dädalus in das Labyrinth eingesperrt hat. Auf Kreta war Dädalus wegen des Mordes an seinem Neffen, der ihn an Kreativität und Handwerkskunst zu übertreffen drohte, angeklagt. Aus Neid brachte er diesen um und wurde verbannt. Eine Möglichkeit wäre für mich, das Minos herausgefunden hat, dass Dädalus seiner Gemahlin geholfen hat und eine andere ist die, das Dädalus Theseus geholfen hat den Minotaurus zu besiegen und deshalb in die Irren seines eigenen Machwerks verbannt wurde. Wer es besser weiß möge es mir bitte mitteilen! Ich habe für diese Passage keinen guten Beleg gefunden und kann an dieser Stelle nur gefährliches Nichtwissen anbringen.")]),i("p",[e._v("Nun gut, also Dädalus und sein Sohn Ikarus saßen also im Labyrinth. An diesem Punkt kommt nun das eigentliche Kernstück zum Tragen:\n„Dädalus […] änderte schlau die Natur. Denn er stellte, von der kleinen beginnend, Federn zusammen in Reih´[…] unten verband er sie dann mit Wachs, mit Zwirn in der Mitte […]“ (Achtes Buch, V 79). Er schuf sich und seinem Sohn also eine Möglichkeit, um dem Irrgarten zu entfliehen. Er baute beiden ein paar Flügel und sie entkamen auch, allerdings wollte Ikarus partout nicht den Weisungen seines Vaters, dem Weg der Mitte, folgen.")]),e._m(1),i("p",[e._v("Ende vom Lied Ikarus ertrank und Dädalus entkam allein.")]),i("h3",{attrs:{id:"was-konnte-uns-der-autor-damit-sagen-wollen-bzw-was-konnten-deutungen-hinter-dieser-sage-sein"}},[e._v("Was könnte uns der Autor damit sagen wollen, bzw. was könnten Deutungen hinter dieser Sage sein?")]),i("p",[e._v("In seine Ursprünglichkeit war die Ikarus Sage als mittelalterliches Warnbild gemeint, nicht umsonst verweist Dädalus darauf, dass der Mittelweg der beste sei und das Streben zu tief und zu hoch bestraft würde. Im Laufe der Zeit wurde dieses Bild jedoch gewandelt und es ist somit auch eine positive Deutung möglich. Ikarus der Schöne, Kühne, Ehrgeizige. Das Paradoxe daran ist die Ambivalenz zwischen dummen ungehorsamen Knaben und der Heroisierung seines Ungehorsams gegenüber seinem Vater. Es steht also auf der einen Seite einmal das unglückliche Opfer und auf der anderen Seite der glückliche Held. (vgl. Rutenfranz Maria, 2004)")]),i("p",[e._v("Gerhard Holtz-Baumert erzählt den Mythos als die Geschichte des Dädalus, der als Stammvater moderner Ingenieurskunst zu betrachten ist. Damit wird die Sage zum Sinnbild für die Schöpferkraft des Menschen.")]),i("p",[e._v("Andere sehen einen Zusammenhang zwischen den Untaten des Vaters und dem Sturz des Sohnes bzw. Fluch oder Segen, die der Erfinderlust des Dädalus innewohnt. (vgl. Rutenfranz Maria, 2004)")]),i("h3",{attrs:{id:"quellen"}},[e._v("Quellen")]),e._m(2),e._m(3),e._m(4),e._m(5),i("unsplash",{attrs:{id:"wPlzrculha8",author:"Stephen Leonardi"}})],1)},[function(){var e=this.$createElement,n=this._self._c||e;return n("blockquote",[n("p",[this._v("Wissen ist ein göttliches Geschenk, und göttlich ist der Wissensdrang, doch gilt das Gebot die eigenen Grenzen nicht zu übertreten. Wer nur für sich selbst denkt, ohne es einer rechten Prüfung zu unterziehen, schenkt – als ein Ikarus – ikarischen Gewässern seinen Namen. (Aurnhammer Martin, 1998)")])])},function(){var e=this.$createElement,n=this._self._c||e;return n("blockquote",[n("p",[this._v("Als am verwegenen Flug sich der Knabe begann zu ergötzen, keck den Führer verließ und von Lust nach dem Himmel verleitet Höheren Weg einschlug. Weich wird von der Nähe der Sonne das duftende Wachs […] IKARUS schallt sein Ruf. Da sieht er im Wasser die Federn, und er verwünscht die erfundene Kunst […] (Ovid,Metamorphosen 8. Buch)")])])},function(){var e=this.$createElement,n=this._self._c||e;return n("p",[this._v("Achim Arnhammer, Dieter Martin (1998): "),n("em",[this._v("Mythos Ikarus. Texte von Ovid bis Wolf Biermann")]),this._v(", Reclam Verlag Leipzig. Leipzig (S.48)")])},function(){var e=this.$createElement,n=this._self._c||e;return n("p",[this._v("Maria Rutenfranz (2004): "),n("em",[this._v("Kinder- und Jugendkultur und -medien. Theorie-Geschichte-Didaktik. Götter und Adaption antiker Mythologie in der deutschen Kinder- und Jugendliteratur,")]),this._v(" Peter Lang Verlag. Frankfurt am Main (S.274f.)")])},function(){var e=this.$createElement,n=this._self._c||e;return n("p",[this._v("Martin Vöhler, Bernd Seidensticker (2005): "),n("em",[this._v("Mythenkorrektur. Zu einer paradoxen Form der Mythenrezeption,")]),this._v(" Water de Guyter. Berlin")])},function(){var e=this.$createElement,n=this._self._c||e;return n("p",[this._v("Ovid: "),n("em",[this._v("Methamorphosen")])])}],!1,null,null,null);n.default=t.exports}}]);